                })
            })
                .then(response => response.json())
                .then(requestJSON => this.saveResults(requestJSON))

        } else {
            console.log("Location was not selected!");
        }
    }

    saveResults = (requestJSON) => {
        this.props.setUserID(requestJSON['guestID'])
    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <input
                    type='text'
                    placeholder='Location'
                    value={this.state.location}
                    onChange={this.onChange} >
                </input>

                <Button
                    type='submit'
                    value='Submit'
                    variant='success'
                    onClick={this.onSubmit}
                >
                    Proceed
                </Button>

                {/* <Gallery
                    index={this.state.imageIndex}
                    onRequestChange={i => {
                        this.setState({ imageIndex: i });
                    }}
                >
                    {this.state.images.map(image => (
                        <GalleryImage key={image} src={image} />
                    ))}
                </Gallery> */}
            </form >
        )
    }
}


export default AddLocation;
