import React, { Component } from 'react';
import Button from 'react-bootstrap/Button'
import { Gallery, GalleryImage } from 'react-gesture-gallery'
// import PropTypes from 'prop-types';

import breakfastButtonImage from '../static/breakfastButtonImage.jpg';
import lunchButtonImage from '../static/lunchButtonImage.png';
import dinnerButtonImage from '../static/dinnerButtonImage.jpg';

function Example() {
    const [index, setIndex] = React.useState(0);

    const images = [
        {
            src:
                "https://images.unsplash.com/photo-1557958114-3d2440207108?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1950&q=80"
        },
        {
            src:
                "https://images.unsplash.com/photo-1557939403-1760a0e47505?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1931&q=80"
        },
        {
            src:
                "https://images.unsplash.com/photo-1558029062-a37889b87526?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=975&q=80"
        },
        {
            src:
                "https://images.unsplash.com/photo-1558088458-b65180740294?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1579&q=80"
        },
        {
            src:
                "https://images.unsplash.com/photo-1558039719-79cb7b60d279?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1950&q=80"
        }
    ];

    return (
        <div style={{ background: "black", width: "100vw", height: "100vh" }}>
            <Gallery
                index={index}
                onRequestChange={i => {
                    setIndex(i);
                }}
            >
                {images.map(img => (
                    <GalleryImage objectFit="contain" key={img.src} src={img.src} />
                ))}
            </Gallery>
        </div>
    );
}

export class AddLocation extends Component {
    state = {
        location: '',
        url: "https://howardlam546.pythonanywhere.com",
        images: [breakfastButtonImage, lunchButtonImage, dinnerButtonImage],
        imageIndex: 0
    }

    getStyle = () => {
        return {
            padding: 25,
            flex: 2
        }
    }

    onChange = (e) => {
        this.setState({ location: e.target.value });
    }

    onSubmit = (e) => {
        if (this.state.location !== '') {
            this.props.setLocation(this.state.location);
            this.props.switchToState("selection");
            fetch((this.state.url + '/guest/new'), {
                method: 'POST',

                body: JSON.stringify({



                })
            })
                .then(response => response.json())
                .then(requestJSON => this.saveResults(requestJSON))

        } else {
            console.log("Location was not selected!");
        }
    }

    saveResults = (requestJSON) => {
        this.props.setUserID(requestJSON['guestID'])
    }

    shouldComponentUpdate(nextProps) {
        return false;
    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <input
                    type='text'
                    placeholder='Location'
                    value={this.state.location}
                    onChange={this.onChange} >
                </input>

                <Button
                    type='submit'
                    value='Submit'
                    variant='success'
                    onClick={this.onSubmit}
                >
                    Proceed
                </Button>
                <div>
                    Example()
                </div>
                <Gallery
                    index={this.state.imageIndex}
                    onRequestChange={i => {
                        this.setState({ imageIndex: i });
                    }}
                >
                    {this.state.images.map(image => (
                        <GalleryImage key={image} src={image} />
                    ))}
                </Gallery>
            </form >
        )
    }
}


export default AddLocation;
